var noble = require('noble');
noble.on('stateChange', function() {
  noble.startScanning();
  noble.on('discover', function(p) {
    console.log('discovered peripheral', p.id);
    console.log(p.address);
    console.log(p.advertisement.localName);
    console.log(p.advertisement.manufacturerData);
    console.log(p);
  });
});
